mapper.select:
		
		  	select no, name
 			from users
 			where email = #{email} and password =#{password}

mapper.update:

  	update users
  	set name = #{name}, email = #{email}, password = #{password}, gender = #{gender}
  	where no = #{no}

mapper.select:

  	select no, name, email, gender
	from users 
	where email = #{email}
